mapper.select:
SELECT COUNT(*) FROM money_check.monthly_expenditure_2503 me -- 년월일 고민해야함 WHERE me.is_active = true AND me.user_id = #{userId}
AND me.asset_id = #{assetId}
AND me.content LIKE '%' || #{searchWord} || '%'

SELECT u.nickname, me.expenditure_id as expenditureId, ag.name as assetName, ec.name as expenditureClassName, me.amount, me.expenditure_at as expenditureAt, me.content, me.created_at as createdAt, me.updated_at as updatedAt FROM money_check.monthly_expenditure_2503 me JOIN money_check.users u ON u.user_id = me.user_id JOIN money_check.asset_group ag ON ag.asset_id = me.asset_id JOIN money_check.expenditure_classification ec ON ec.expenditure_class_id = me.expenditure_class_id WHERE me.is_active = true AND me.user_id = #{userId}
AND me.asset_id = #{assetId}
AND me.content LIKE '%' || #{searchWord} || '%'
